var labelArray = ["", ];
var relatedPostConfig = {
homePage: "",
widgetTitle: "<div class='label-line-c'><h4>Artikel TerkaitRelated Posts</h4></div>",
numPosts: 8,
titleLength: "auto",
thumbnailWidth: 250,
thumbnailHeight: 170,
noImage: "//3.bp.blogspot.com/-ltyYh4ysBHI/U04MKlHc6pI/AAAAAAAADQo/PFxXaGZu9PQ/w255-h170-c/no-image.png",
containerId: "related-post-",
